package org.example.controller.user;import org.example.config.ScannerConsole;import org.example.controller.CommandImpl;import org.example.controller.Controller;import java.util.Scanner;public class UserCreateControllerImpl implements Controller {  private final Scanner scanner = ScannerConsole.getInstance();  private static UserCreateControllerImpl userCreateController;  public static UserCreateControllerImpl getUserCreateController() {    if (userCreateController == null) {      userCreateController = new UserCreateControllerImpl();    }    return userCreateController;  }  @Override  public void start() {    System.out.print(        "\n \uD83D\uDC49 USER\n \uD83D\uDC49 LIST \n   \uD83D\uDC49 BACK \n   \uD83D\uDC49 STOP\n\uD83D\uDC49 ");    String next = scanner.next();    if (next.equalsIgnoreCase("user")) {      UserCreateNewUserControllerImpl.getUserCreateController().start();      start();    }    if (next.equalsIgnoreCase("list")) {      UserCreateUsersListControllerImpl.getUserCreateUsersListController().start();      start();    }    if (next.equalsIgnoreCase("back")) {      UserControllerImpl.getUserController().start();    }    if (next.equalsIgnoreCase("stop")) {      stop();    } else {      System.out.print("\n      ⚠️ Wrong ⚠️ \n \uD83D\uDCAC Please, enter again \n");      start();    }  }  @Override  public void stop() {    System.exit(0);  }}