package org.example.controller.user;import lombok.NoArgsConstructor;import org.example.config.ScannerConsole;import org.example.controller.Controller;import java.util.Scanner;@NoArgsConstructorpublic class UserCreateControllerImpl implements Controller {  private static UserCreateControllerImpl userCreateController;  private final Scanner scanner = ScannerConsole.getInstance();  private final UserCreateNewUserControllerImpl createNewUserController = UserCreateNewUserControllerImpl.getUserCreateController();  private final UserCreateUsersListControllerImpl createUsersListController = UserCreateUsersListControllerImpl.getUserCreateUsersListController();  public static UserCreateControllerImpl getUserCreateController() {    if (userCreateController == null) {      userCreateController = new UserCreateControllerImpl();    }    return userCreateController;  }  @Override  public void start() {    System.out.print(        "\n \uD83D\uDC49 NewUSER\n \uD83D\uDC49 ListUSERS \n   \uD83D\uDC49 BACK \n   \uD83D\uDC49 STOP\n\uD83D\uDC49 ");    String next = scanner.next();    if (next.equalsIgnoreCase("NewUSER")) {      createNewUserController.start();      start();    }    if (next.equalsIgnoreCase("ListUSERS")) {      createUsersListController.start();      start();    }    if (next.equalsIgnoreCase("stop")) {      stop();    }    if (next.equalsIgnoreCase("back")) {      UserControllerImpl.getUserController().start();    } else {      System.out.print("\n      ⚠️ Wrong ⚠️ \n \uD83D\uDCAC Please, enter again \n");      start();    }  }  @Override  public void stop() {    System.exit(0);  }}